CREATE FUNCTION [dbo].[CanOpenCourseForGraduation](@StudentID INT,@PassedHours TINYINT,@ProgramID INT,@Hours INT)
RETURNS BIT
AS
BEGIN
	DECLARE @CurrentSemesterRegistedHours TINYINT,
			@ProgramTotalHours TINYINT;
	SELECT @CurrentSemesterRegistedHours = ISNULL(SUM(c.CreditHours),0)
	FROM StudentCourses sc JOIN Course c ON sc.CourseID = c.ID 
	WHERE 
		sc.StudentID = @StudentID AND
		sc.AcademicYearID = [dbo].[GetCurrentYearID]()
	
	SELECT @ProgramTotalHours = TotalHours  
	FROM Program
	WHERE ID = @ProgramID;

	IF @PassedHours + @CurrentSemesterRegistedHours BETWEEN @ProgramTotalHours - @Hours AND @ProgramTotalHours
	BEGIN
		RETURN 1;
	END
	RETURN 0;
END
GO
