		(SELECT MAX(AcademicYearID)FROM StudentCourses WHERE StudentID = @StudentID)
		WHERE ID = @StudentID AND Level = 4
	END
	EXECUTE [dbo].[UpdateStudentComputedValues] @StudentID
END
