CREATE PROCEDURE [dbo].[AddStudentsToPrograms]
@StudentProgram StudentsProgramsType READONLY
AS 
BEGIN
	BEGIN TRANSACTION
	BEGIN TRY
	
		INSERT INTO [dbo].StudentPrograms
			([ProgramID], [StudentID], [AcademicYear])
		SELECT [ProgramID], [StudentID], [AcademicYearID] FROM @StudentProgram;
		DECLARE @StudentID INT;
		
		DECLARE CurrentProgramUpdateCursor CURSOR FOR  
		SELECT DISTINCT(StudentID)
		FROM @StudentProgram;

		OPEN CurrentProgramUpdateCursor;  
		FETCH NEXT FROM CurrentProgramUpdateCursor 
		INTO @StudentID;
			WHILE @@FETCH_STATUS = 0 
				BEGIN
					UPDATE Student
						SET CurrentProgramID = (SELECT TOP (1) ProgramID
										FROM StudentPrograms 
										WHERE StudentID = @StudentID
										ORDER BY AcademicYear DESC)
						WHERE ID = @StudentID;
				FETCH NEXT FROM CurrentProgramUpdateCursor 
					INTO @StudentID;
				END;  
			CLOSE CurrentProgramUpdateCursor;  
		DEALLOCATE CurrentProgramUpdateCursor;

	END TRY
	BEGIN CATCH
		IF @@ERROR <> 0
			ROLLBACK;
	END CATCH
	COMMIT;
END
