	END
	ELSE
	BEGIN
		SELECT ec.ProgramID,
		ec.Level,
		ec.Semester,
		ec.Category,
		ec.CourseType,
		ec.Hour
		FROM ElectiveCourseDistribution ec
		WHERE ec.ProgramID = @ProgramID
	END
END
