	------------------------------------------
	SELECT [Level],[Semester],[NumberOfHours]
		FROM [dbo].[ProgramDistribution]
		WHERE ProgramID = @ProgramID;
	------------------------------------------
	SELECT c.CourseCode,c.CourseName, pc.PrerequisiteRelationID,c.level,c.Semester,c.CreditHours,
			pc.CourseType,pc.Category,pc.DeletionYearID,pc.AddtionYearID,
			--SQL 2016
