						FROM CoursePrerequisites cp 
						LEFT JOIN Course ipc ON cp.PrerequisiteCourseID = ipc.ID
						WHERE cp.CourseID = c.ID AND ProgramID = @ProgramID
			for xml path(''),type).value('.','varchar(MAX)'),1,1,'') as Ids) AS Prerequisites,
			-- SQL 2017 AND Above
			--(SELECT STRING_AGG(ipc.CourseCode, ',')
			--		FROM CoursePrerequisites cp 
			--			LEFT JOIN Course ipc ON cp.PrerequisiteCourseID = ipc.ID
			--			WHERE cp.CourseID = c.ID AND ProgramID = @ProgramID
			--		) AS Prerequisites,
			(SELECT ecd.Hour FROM ElectiveCourseDistribution ecd 
						WHERE ProgramID = @ProgramID AND ecd.Level = c.Level 
						AND ecd.Semester = c.Semester 
						AND ecd.CourseType = pc.CourseType AND ecd.Category = pc.Category) AS AllowedHours
			FROM ProgramCourses pc LEFT JOIN Course c ON c.ID = pc.CourseID
