CREATE PROCEDURE [dbo].[GetProgramsListForProgramTransfer]
@ProgramID INT
AS
BEGIN
	WITH ParentChilds AS (
				SELECT *
				FROM Program
				WHERE ID = @ProgramID
			UNION ALL
				SELECT child.*
				FROM Program child
				JOIN ParentChilds pc
				  ON pc.SuperProgramID = child.ID
								)
	SELECT ID,Name 
	FROM Program
	WHERE IsGeneral = 0 AND ID NOT IN(
			SELECT ID
			FROM ParentChilds
	);
END
