CREATE PROCEDURE [dbo].[Report_GetStudentsByCGPA]
@FromCgpa DECIMAl(5,4) = NULL,
@ToCgpa DECIMAl(5,4) = NULL,
@Level TINYINT = NULL,
@ProgramID NVARCHAR(MAX) = NULL,
@IsGraduated BIT = 0,
@StartGraduationYearID SMALLINT = NULL,
@EndGraduationYearID SMALLINT = NULL
AS
BEGIN
	IF @IsGraduated = 0
		BEGIN
			SET @StartGraduationYearID = NULL;
			SET @EndGraduationYearID = NULL;
		END
	ELSE
		BEGIN
			IF @StartGraduationYearID IS NULL
				BEGIN
					SELECT TOP 1 @StartGraduationYearID = ID FROM AcademicYear ORDER BY ID ASC
				END
			IF @EndGraduationYearID IS NULL
				BEGIN
					SELECT TOP 1 @EndGraduationYearID = ID FROM AcademicYear ORDER BY ID DESC
				END
			SET @Level = 4
		END
	IF (@FromCgpa IS NULL)
		BEGIN
			SET @FromCgpa = 0;
		END
	IF(@ToCgpa IS NULL)
		BEGIN
			SET @ToCgpa = 4;
		END;
	SELECT s.Name,
	s.PhoneNumber,
	s.AcademicCode,
	s.CGPA,
	s.PassedHours,
	s.Level,
	eyi.AcademicYear AS [EnrollYear],
	gyi.AcademicYear AS [GraduationYear],
	gyi.Semester AS [GraduationSemester],
	p.Name AS [ProgramName]
	FROM Student s LEFT JOIN AcademicYear eyi ON eyi.ID = s.EnrollYearID
	LEFT JOIN AcademicYear gyi ON s.GraduatedYearID = gyi.ID
	LEFT JOIN Program p ON p.ID = s.CurrentProgramID
	WHERE s.CGPA BETWEEN @FromCgpa AND @ToCgpa
	AND (@Level IS NULL OR s.Level = @Level)
	AND (@ProgramID IS NULL OR s.CurrentProgramID IN (SELECT * FROM [dbo].[GetSubProgramsIDs](@ProgramID)))
	AND 
	((@IsGraduated = 0 AND s.IsGraduated = 0) 
	OR @IsGraduated = 1 AND s.IsGraduated = 1 
		AND (s.GraduatedYearID BETWEEN @StartGraduationYearID AND @EndGraduationYearID))
	ORDER BY s.CGPA DESC
END
GO
