					BEGIN
						SET @IsGpaIncluded =0;
					END


			END
			UPDATE StudentCourses WITH (TABLOCK)
			SET
			Mark =@Mark,
			Grade=@Grade,
			Points = @Points,
			TookFromCredits = @TookFromCredits,
			QualityPoints = @Points * @CreditHours
			WHERE StudentID=@StudentID AND CourseID =@CourseID AND AcademicYearID = @AcademicYearID;
END
