CREATE PROC [dbo].[UpdateFullProgram]
@ProgramID INT,
@Name NVARCHAR(MAX),
@Semester TINYINT,
@Percentage FLOAT,
@IsRegular BIT,
@IsGeneral BIT,
@TotalHours TINYINT,
@EnglishName VARCHAR(MAX),
@ArabicName NVARCHAR(MAX),
@SuperProgramID INT = NULL,
@CoursesList ProgramCoursesType READONLY,
@PrerequisiteCoursesList PrerequisiteCoursesType READONLY,
@ProgramDistributionList ProgramDistributionType READONLY,
@ElectiveCourseDistributionList ElectiveCourseDistributionType READONLY
AS
BEGIN
	UPDATE [dbo].[Program]
		SET [Name] = @Name
			,[Semester] = @Semester
			,[Percentage] = @Percentage
			,[IsRegular] = @IsRegular
			,[IsGeneral] = @IsGeneral
			,[TotalHours] = @TotalHours
			,[EnglishName] = @EnglishName
			,[ArabicName] = @ArabicName
			,[SuperProgramID] = @SuperProgramID
		 WHERE ID = @ProgramID;
	---------------------------------------------
		DECLARE @CourseID INT,@CourseType TINYINT,@PrerequisiteRelationID TINYINT,
				@Category TINYINT,@AddtionYearID SMALLINT = NULL,@DeletionYearID SMALLINT = NULL;

		DECLARE ProgramCoursesCursor CURSOR FOR  
		SELECT CourseID,CourseType,PrerequisiteRelationID,Category,AddtionYearID,DeletionYearID
		FROM @CoursesList;

		OPEN ProgramCoursesCursor;  
		FETCH NEXT FROM ProgramCoursesCursor 
		INTO @CourseID,@CourseType,@PrerequisiteRelationID,@Category,@AddtionYearID,@DeletionYearID;
		WHILE @@FETCH_STATUS = 0 
			BEGIN
				UPDATE [dbo].[ProgramCourses]
				   SET 
					  [PrerequisiteRelationID] = @PrerequisiteRelationID
					  ,[CourseType] = @CourseType
					  ,[Category] = @Category
					  ,[DeletionYearID] = @DeletionYearID
					  ,[AddtionYearID] = @AddtionYearID
				WHERE ProgramID = @ProgramID AND CourseID = @CourseID;
				
				FETCH NEXT FROM ProgramCoursesCursor 
				INTO @CourseID,@CourseType,@PrerequisiteRelationID,@Category,@AddtionYearID,@DeletionYearID;
			END;  
			CLOSE ProgramCoursesCursor;  
		DEALLOCATE ProgramCoursesCursor;
	---------------------------------------------
	DELETE FROM CoursePrerequisites 
	WHERE ProgramID = @ProgramID AND CourseID IN (SELECT CourseID FROM @CoursesList);
	INSERT INTO CoursePrerequisites(ProgramID,CourseID,PrerequisiteCourseID)
		SELECT @ProgramID,CourseID,PrerequisiteCourseID
		FROM @PrerequisiteCoursesList;
	---------------------------------------------
	DELETE FROM ProgramDistribution WHERE ProgramID = @ProgramID;
	INSERT INTO ProgramDistribution(ProgramID,Level,Semester,NumberOfHours)
		SELECT @ProgramID,Level,Semester,NumberOfHours
		FROM @ProgramDistributionList WHERE NumberOfHours <> 0;
	---------------------------------------------
	DELETE FROM ElectiveCourseDistribution WHERE ProgramID = @ProgramID;
	INSERT INTO ElectiveCourseDistribution(ProgramID,Level,Semester,CourseType,Category,Hour)
		SELECT @ProgramID,Level,Semester,CourseType,Category,Hour
		FROM @ElectiveCourseDistributionList;
END
